mapper.delete:
delete from t_borrow_info
where book_id in

    #{item}

mapper.select:
SELECT b_i.borrow_id, b_i.lend_time, b_i.give_back_time, b_i.reader_id, b_i.book_id, b_i.be_overdue, b_i.fine, b_i.be_overdue_day, b_i.whether_lend, b.`book_name`, r.`name` FROM t_borrow_info AS b_i LEFT JOIN t_book AS b ON b_i.`book_id`=b.`book_id` LEFT JOIN t_reader AS r ON b_i.`reader_id`=r.`reader_id`
INSTR (b.book_name,#{keyword}) OR INSTR(r.name,#{keyword})
order by book_id ASC limit ${from},${pageSize}

mapper.update:
update t_borrow_info
set whether_lend=#{whetherLend}
where borrow_id=#{borrowId}
