mapper.delete:
delete from t_reader
where reader_id in

    #{item}

mapper.select:
select r.reader_id, r.password, r.name, r.gender, r.avatar, r.phone, r.college_id, r.major_id,r.class_id, g.gender_name,co.college_name,m.major_name,cl.class_name from t_reader as r left join t_gender as g on r.gender=g.id LEFT JOIN t_college AS co ON r.college_id = co.id left join t_class as cl on r.class_id=cl.id LEFT JOIN t_major AS m ON r.major_id = m.id
INSTR (r.name,#{keyword}) or INSTR(r.reader_id,#{keyword}) or INSTR(cl.class_name,#{keyword})
order by r.reader_id ASC limit ${from},${pageSize}
